    // keccak256(abi.encodePacked("nullifierSessionID"))
    bytes32 private constant NULLIFIERSESSIONID_NAME =
        0x24cea8e4716dcdf091e4abcbd3ea617d9a5dd308b90afb5da0d75e56b3c0bc95;

    /**
     * @dev Initialize the contract
     * @param stateContractAddr Address of the state contract
     * @param verifierContractAddresses Addresses of the verifier contracts for the supported circuits
     * @param circuitIds Circuit ids of the supported circuits
     * @param owner Owner of the contract
     */
    function initialize(
        address stateContractAddr,
        address[] calldata verifierContractAddresses,
        string[] calldata circuitIds,
        address owner
    ) public initializer {
        _setInputToIndex("userID", 0);
        _setInputToIndex("circuitQueryHash", 1);
        _setInputToIndex("issuerState", 2);
        _setInputToIndex("linkID", 3);
        _setInputToIndex("nullifier", 4);
        _setInputToIndex("operatorOutput", 5);
        _setInputToIndex("proofType", 6);
        _setInputToIndex("requestID", 7);
        _setInputToIndex("challenge", 8);
        _setInputToIndex("gistRoot", 9);
        _setInputToIndex("issuerID", 10);
        _setInputToIndex("issuerClaimNonRevState", 11);
        _setInputToIndex("timestamp", 12);
        _setInputToIndex("isBJJAuthEnabled", 13);

        _initDefaultStateVariables(stateContractAddr, verifierContractAddresses, circuitIds, owner);
    }

    /**
     * @dev Get the version of the contract
     * @return Version of the contract
     */
    function version() public pure override returns (string memory) {
        return VERSION;
    }

    /**
     * @dev Parse the public signals
     * @param inputs Array of public inputs
     * @return Parsed public signals
     */
    function parsePubSignals(uint256[] memory inputs) public pure returns (PubSignals memory) {
        PubSignals memory pubSignals = PubSignals({
            userID: inputs[0],
            circuitQueryHash: inputs[1],
            issuerState: inputs[2],
            linkID: inputs[3],
            nullifier: inputs[4],
            operatorOutput: inputs[5],
            proofType: inputs[6],
            requestID: inputs[7],
            challenge: inputs[8],
            gistRoot: inputs[9],
            issuerID: inputs[10],
            issuerClaimNonRevState: inputs[11],
            timestamp: inputs[12],
            isBJJAuthEnabled: inputs[13]
        });

        return pubSignals;
    }

    /**
     * @dev Verify the proof with the supported method informed in the request query data
     * packed as bytes and that the proof was generated by the sender.
     * @param sender Sender of the proof.
     * @param proof Proof packed as bytes to verify.
     * @param requestParams Request query data of the credential to verify.
     * @param responseMetadata Metadata from the response.
     * @return Array of response fields as result.
     */
    function verify(
        address sender,
        bytes calldata proof,
        bytes calldata requestParams,
        bytes calldata responseMetadata
    ) public view override returns (IRequestValidator.ResponseField[] memory) {
        CredentialAtomicQueryV3 memory credAtomicQuery = abi.decode(
            requestParams,
            (CredentialAtomicQueryV3)
        );
        return
            _checkPubSignalsWithCredentialAtomicQuery(
                credAtomicQuery,
                _verifyProof(proof, credAtomicQuery, responseMetadata),
                sender
            );
    }

    /**
     * @dev Get the request param from params of the request query data.
     * @param params Request query data of the credential to verify.
     * @param paramName Request query param name to retrieve of the credential to verify.
     * @return RequestParam for the param name of the request query data.
     */
    function getRequestParam(
        bytes calldata params,
        string memory paramName
    ) external pure returns (RequestParam memory) {
        CredentialAtomicQueryV3 memory query = abi.decode(params, (CredentialAtomicQueryV3));

        if (keccak256(bytes(paramName)) == GROUPID_NAME) {
            return IRequestValidator.RequestParam({name: paramName, value: query.groupID});
        }
        if (keccak256(bytes(paramName)) == VERIFIERID_NAME) {
            return IRequestValidator.RequestParam({name: paramName, value: query.verifierID});
        }
        if (keccak256(bytes(paramName)) == NULLIFIERSESSIONID_NAME) {
            return
                IRequestValidator.RequestParam({name: paramName, value: query.nullifierSessionID});
        }

        revert RequestParamNameNotFound();
    }

    function _verifyProof(
        bytes calldata proof,
        CredentialAtomicQueryV3 memory credAtomicQuery,
        bytes calldata responseMetadata
    ) internal view returns (PubSignals memory) {
        if (credAtomicQuery.circuitIds.length != 1) {
            revert CircuitsLengthShouldBeOne();
        }

        (
            uint256[] memory inputs,
            uint256[2] memory a,
            uint256[2][2] memory b,
            uint256[2] memory c
        ) = abi.decode(proof, (uint256[], uint256[2], uint256[2][2], uint256[2]));

        // This validator expects circuitId in the response metadata to select especific verifier
        string memory circuitId = abi.decode(responseMetadata, (string));

        IGroth16Verifier g16Verifier = getVerifierByCircuitId(circuitId);
        if (g16Verifier == IGroth16Verifier(address(0))) {
            revert VerifierAddressShouldNotBeZero();
        }

        // verify that zkp is valid
        if (!g16Verifier.verify(a, b, c, inputs)) {
            revert ProofIsNotValid();
        }

        return parsePubSignals(inputs);
    }

    function _checkPubSignalsWithCredentialAtomicQuery(
        CredentialAtomicQueryV3 memory credAtomicQuery,
        PubSignals memory pubSignals,
        address sender
    ) internal view returns (IRequestValidator.ResponseField[] memory) {
        _checkAllowedIssuers(pubSignals.issuerID, credAtomicQuery.allowedIssuers);
        _checkProofExpiration(pubSignals.timestamp);

        _checkGroupIDOrLinkID(credAtomicQuery.groupID, pubSignals.linkID);
        _checkProofType(credAtomicQuery.proofType, pubSignals.proofType);
        _checkNullify(pubSignals.nullifier, credAtomicQuery.nullifierSessionID);

        // GIST root and state checks
        _checkClaimIssuanceState(pubSignals.issuerID, pubSignals.issuerState);
        _checkClaimNonRevState(pubSignals.issuerID, pubSignals.issuerClaimNonRevState);
        if (pubSignals.isBJJAuthEnabled == 1) {
            _checkGistRoot(pubSignals.userID, pubSignals.gistRoot);
        } else {
            _checkAuth(pubSignals.userID, sender);
        }

        // Checking challenge to prevent replay attacks from other addresses
        _checkChallenge(pubSignals.challenge, sender);

        // check circuitQueryHash
        if (pubSignals.circuitQueryHash != credAtomicQuery.queryHash) {
            revert QueryHashDoesNotMatchTheRequestedOne(
                credAtomicQuery.queryHash,
                pubSignals.circuitQueryHash
            );
        }

        // if operator == 16 then we have selective disclosure
        return _getResponseFields(pubSignals, credAtomicQuery.operator == 16);
    }

    function _checkGroupIDOrLinkID(uint256 groupID, uint256 linkID) internal pure {
        if (groupID == 0 && linkID == 0) return;
        if (groupID != 0 && linkID != 0) return;
        revert InvalidGroupIDOrLinkID(groupID, linkID);
    }

    function _checkProofType(uint256 queryProofType, uint256 pubSignalProofType) internal pure {
        if (queryProofType != 0 && queryProofType != pubSignalProofType) {
            revert ProofTypeShouldMatchTheRequestedOneInQuery();
        }
    }

    function _checkNullify(uint256 nullifier, uint256 nullifierSessionID) internal pure {
        if (nullifierSessionID != 0 && nullifier == 0) {
            revert InvalidNullifyPubSignal();
        }
    }

    function _checkAuth(uint256 userID, address ethIdentityOwner) internal pure {
        if (
            userID !=
            GenesisUtils.calcIdFromEthAddress(GenesisUtils.getIdType(userID), ethIdentityOwner)
        ) {
            revert UserIDDoesNotCorrespondToTheSender();
        }
    }

    function _getResponseFields(
        PubSignals memory pubSignals,
        bool hasSelectiveDisclosure
    ) internal pure returns (IRequestValidator.ResponseField[] memory) {
        uint256 numSignals = hasSelectiveDisclosure ? 7 : 6;
        IRequestValidator.ResponseField[]
            memory responseFields = new IRequestValidator.ResponseField[](numSignals);

        uint256 i = 0;
        responseFields[i++] = IRequestValidator.ResponseField({
            name: "userID",
            value: pubSignals.userID,
            rawValue: ""
        });
        responseFields[i++] = IRequestValidator.ResponseField({
            name: "linkID",
            value: pubSignals.linkID,
            rawValue: ""
        });
        responseFields[i++] = IRequestValidator.ResponseField({
            name: "nullifier",
            value: pubSignals.nullifier,
            rawValue: ""
        });
        responseFields[i++] = IRequestValidator.ResponseField({
            name: "timestamp",
            value: pubSignals.timestamp,
            rawValue: ""
        });
        responseFields[i++] = IRequestValidator.ResponseField({
            name: "issuerID",
            value: pubSignals.issuerID,
            rawValue: ""
        });
        responseFields[i++] = IRequestValidator.ResponseField({
            name: "isEmbeddedAuthVerified",
            value: 1,
            rawValue: ""
        });
        if (hasSelectiveDisclosure) {
            responseFields[i++] = IRequestValidator.ResponseField({
                name: "operatorOutput",
                value: pubSignals.operatorOutput,
                rawValue: ""
            });
        }
        return responseFields;
    }
}

