    ) external view virtual returns (ResponseField[] memory);

    /**
     * @dev See {IERC165-supportsInterface}.
     */
    function supportsInterface(bytes4 interfaceId) public view virtual override returns (bool) {
        return
            interfaceId == type(IRequestValidator).interfaceId ||
            super.supportsInterface(interfaceId);
    }

    function _initDefaultStateVariables(
        address stateContractAddr,
        address[] memory verifierContractAddresses,
        string[] memory circuitIds,
        address owner
    ) internal {
        if (verifierContractAddresses.length != circuitIds.length) {
            revert VerifierAddressesAndCircuitIDsMismatch();
        }
        CredentialAtomicQueryValidatorBaseStorage
            storage s = _getCredentialAtomicQueryValidatorBaseStorage();

        s.revocationStateExpirationTimeout = 1 hours;
        s.proofExpirationTimeout = 1 hours;
        s.gistRootExpirationTimeout = 1 hours;
        s.state = IState(stateContractAddr);
        for (uint256 i = 0; i < circuitIds.length; i++) {
            _setGroth16Verifier(circuitIds[i], IGroth16Verifier(verifierContractAddresses[i]));
        }

        __Ownable_init(owner);
    }

    function _checkGistRoot(uint256 _id, uint256 _gistRoot) internal view {
        CredentialAtomicQueryValidatorBaseStorage
            storage $ = _getCredentialAtomicQueryValidatorBaseStorage();
        bytes2 idType = GenesisUtils.getIdType(_id);
        uint256 replacedAt = $.state.getGistRootReplacedAt(idType, _gistRoot);

        if (replacedAt != 0 && block.timestamp > $.gistRootExpirationTimeout + replacedAt) {
            revert GistRootIsExpired();
        }
    }

    function _getState() internal view returns (IState) {
        return _getCredentialAtomicQueryValidatorBaseStorage().state;
    }

    function _checkClaimIssuanceState(uint256 _id, uint256 _state) internal view {
        _getState().getStateReplacedAt(_id, _state);
    }

    function _checkClaimNonRevState(uint256 _id, uint256 _claimNonRevState) internal view {
        CredentialAtomicQueryValidatorBaseStorage
            storage $ = _getCredentialAtomicQueryValidatorBaseStorage();
        uint256 replacedAt = _getState().getStateReplacedAt(_id, _claimNonRevState);

        if (replacedAt != 0 && block.timestamp > $.revocationStateExpirationTimeout + replacedAt) {
            revert NonRevocationStateOfIssuerIsExpired();
        }
    }

    function _checkProofExpiration(uint256 _proofGenerationTimestamp) internal view {
        /*
            Add 5 minutes to `block.timestamp` to prevent potential issues caused by unsynchronized clocks
            or new transactions being included in the block with a previously defined timestamp.
            https://github.com/ethereum/go-ethereum/issues/24152
        */
        if (_proofGenerationTimestamp > (block.timestamp + 5 minutes)) {
            revert ProofGeneratedInTheFutureIsNotValid();
        }
        if (
            block.timestamp >
            _getCredentialAtomicQueryValidatorBaseStorage().proofExpirationTimeout +
                _proofGenerationTimestamp
        ) {
            revert GeneratedProofIsOutdated();
        }
    }

    function _checkAllowedIssuers(uint256 issuerId, uint256[] memory allowedIssuers) internal pure {
        // empty array is 'allow all' equivalent - ['*']
        if (allowedIssuers.length == 0) {
            return;
        }

        for (uint256 i = 0; i < allowedIssuers.length; i++) {
            if (issuerId == allowedIssuers[i]) {
                return;
            }
        }

        revert IssuerIsNotOnTheAllowedIssuersList();
    }

    function _checkChallenge(uint256 challenge, address sender) internal pure {
        if (PrimitiveTypeUtils.uint256LEToAddress(challenge) != sender) {
            revert ChallengeShouldMatchTheSender();
        }
    }
}

project/contracts/validators/request/Lin.content:
// SPDX-License-Identifier: GPL-3.0
pragma solidity ^0.8.10;

import {ERC165} from "@openzeppelin/contracts/utils/introspection/ERC165.sol";
import {IGroth16Verifier} from "../../interfaces/IGroth16Verifier.sol";
import {IRequestValidator} from "../../interfaces/IRequestValidator.sol";
import {Ownable2StepUpgradeable} from "@openzeppelin/contracts-upgradeable/access/Ownable2StepUpgradeable.sol";
import {Strings} from "@openzeppelin/contracts/utils/Strings.sol";
import {RequestValidatorBase} from "./RequestValidatorBase.sol";

error WrongCircuitID(string circuitID);
error InvalidQueryHash(uint256 expectedQueryHash, uint256 actualQueryHash);
error GroupIDCannotBeZero();
error LinkIDCannotBeZero();
error TooManyQueries(uint256 operatorCount);
error InvalidGroth16Proof();

contract LinkedMultiQueryStableValidator is Ownable2StepUpgradeable, RequestValidatorBase, ERC165 {
    // This should be limited to the real number of queries in which operator != 0
    struct Query {
        uint256[] claimPathKey;
        uint256[] operator; // when checking SD take operator from here
        uint256[] slotIndex;
        uint256[][] value;
        uint256[] queryHash;
        string[] circuitIds;
        uint256 groupID;
        uint256 verifierID;
    }

    // keccak256(abi.encodePacked("groupID"))
    bytes32 private constant GROUPID_NAME =
        0xdab5ca4f3738dce0cd25851a4aa9160ebdfb1678ef20ca14c9a3e9217058455a;
    // keccak256(abi.encodePacked("verifierID"))
    bytes32 private constant VERIFIERID_NAME =
        0xa6ade9d39b76f319076fc4ad150ee37167dd21433b39e1d533a5d6b635762abe;
    // keccak256(abi.encodePacked("nullifierSessionID"))
    bytes32 private constant NULLIFIERSESSIONID_NAME =
        0x24cea8e4716dcdf091e4abcbd3ea617d9a5dd308b90afb5da0d75e56b3c0bc95;

    uint256 internal constant MAX_QUERIES_COUNT = 10;

    struct PubSignals {
        uint256 linkID;
        uint256 merklized;
        uint256[MAX_QUERIES_COUNT] operatorOutput;
        uint256[MAX_QUERIES_COUNT] circuitQueryHash;
    }

    struct VerifierInfo {
        string circuitId;
        address verifierAddress;
        uint256 queriesCount;
    }

    string public constant VERSION = "1.0.0";

    mapping(string => uint256) public circuitIdToQueriesCount;

    /// @custom:oz-upgrades-unsafe-allow constructor
    constructor() {
        _disableInitializers();
    }

    /**
     * @dev Returns the version of the contract
     * @return The version of the contract
     */
    function version() external pure override returns (string memory) {
        return VERSION;
    }

    /**
     * @dev Initialize the contract
     * @param verifiersInfo Array of verifier information structs
     * @param owner Owner of the contract
     */
    function initialize(VerifierInfo[] calldata verifiersInfo, address owner) public initializer {
        for (uint256 i = 0; i < verifiersInfo.length; i++) {
            _setGroth16Verifier(
                verifiersInfo[i].circuitId,
                IGroth16Verifier(verifiersInfo[i].verifierAddress)
            );
            circuitIdToQueriesCount[verifiersInfo[i].circuitId] = verifiersInfo[i].queriesCount;
        }

        _setInputToIndex("linkID", 0);
        _setInputToIndex("merklized", 1);
        for (uint256 i = 0; i < MAX_QUERIES_COUNT; i++) {
            _setInputToIndex(
                string(abi.encodePacked("operatorOutput_", Strings.toString(i))),
                2 + i
            );
            _setInputToIndex(
                string(abi.encodePacked("circuitQueryHash_", Strings.toString(i))),
                12 + i
            );
        }

        __Ownable_init(owner);
    }

    /**
     * @dev Verify the proof with the supported method informed in the request query data
     * packed as bytes and that the proof was generated by the sender.
     * @param sender Sender of the proof.
     * @param proof Proof packed as bytes to verify.
     * @param requestParams Request query data of the credential to verify.
     * @param responseMetadata Metadata from the response.
     * @return Array of response fields as result.
     */
    function verify(
        // solhint-disable-next-line no-unused-vars
        address sender,
        bytes calldata proof,
        bytes calldata requestParams,
        bytes calldata responseMetadata
    ) external view returns (IRequestValidator.ResponseField[] memory) {
        Query memory query = abi.decode(requestParams, (Query));
        (
            uint256[] memory inputs,
            uint256[2] memory a,
            uint256[2][2] memory b,
            uint256[2] memory c
        ) = abi.decode(proof, (uint256[], uint256[2], uint256[2][2], uint256[2]));

        // This validator expects circuitId in the response metadata to select especific verifier
        string memory circuitId = abi.decode(responseMetadata, (string));

        IGroth16Verifier g16Verifier = getVerifierByCircuitId(circuitId);
        if (g16Verifier == IGroth16Verifier(address(0))) {
            revert WrongCircuitID(circuitId);
        }

        PubSignals memory pubSignals = _parsePubSignals(inputs, circuitIdToQueriesCount[circuitId]);

        _checkQueryHash(query, pubSignals);
        _checkGroupIDOrLinkID(query.groupID, pubSignals.linkID);

        if (!g16Verifier.verify(a, b, c, inputs)) {
            revert InvalidGroth16Proof();
        }

        return _getResponseFields(pubSignals, query);
    }

    /**
     * @dev Get the request param from params of the request query data.
     * @param params Request query data of the credential to verify.
     * @param paramName Request query param name to retrieve of the credential to verify.
     * @return RequestParam for the param name of the request query data.
     */
    function getRequestParam(
        bytes calldata params,
        string memory paramName
    ) external pure returns (RequestParam memory) {
        Query memory query = abi.decode(params, (Query));

        if (keccak256(bytes(paramName)) == GROUPID_NAME) {
            return IRequestValidator.RequestParam({name: paramName, value: query.groupID});
        } else if (keccak256(bytes(paramName)) == VERIFIERID_NAME) {
            return IRequestValidator.RequestParam({name: paramName, value: query.verifierID});
        } else if (keccak256(bytes(paramName)) == NULLIFIERSESSIONID_NAME) {
            return IRequestValidator.RequestParam({name: paramName, value: 0});
        }
        revert RequestParamNameNotFound();
    }

    /**
     * @dev See {IERC165-supportsInterface}.
     */
    function supportsInterface(bytes4 interfaceId) public view virtual override returns (bool) {
        return
            interfaceId == type(IRequestValidator).interfaceId ||
            super.supportsInterface(interfaceId);
    }

    function _checkGroupIDOrLinkID(uint256 groupID, uint256 linkID) internal pure {
        if (groupID == 0) revert GroupIDCannotBeZero();
        if (linkID == 0) revert LinkIDCannotBeZero();
    }

    function _checkQueryHash(Query memory query, PubSignals memory pubSignals) internal view {
        uint256 expectedQueriesCount = circuitIdToQueriesCount[query.circuitIds[0]];

        if (query.queryHash.length > expectedQueriesCount) {
            revert TooManyQueries(query.queryHash.length);
        }
        for (uint256 i = 0; i < query.queryHash.length; i++) {
            if (query.queryHash[i] != pubSignals.circuitQueryHash[i]) {
                revert InvalidQueryHash(query.queryHash[i], pubSignals.circuitQueryHash[i]);
            }
        }
    }

    function _parsePubSignals(
        uint256[] memory inputs,
        uint256 queriesCount
    ) internal pure returns (PubSignals memory) {
        uint256[MAX_QUERIES_COUNT] memory opsOutput;
        uint256[MAX_QUERIES_COUNT] memory queryHashes;
        PubSignals memory pubSignals = PubSignals({
            linkID: 0,
            merklized: 0,
            operatorOutput: opsOutput,
            circuitQueryHash: queryHashes
        });

        pubSignals.linkID = inputs[0];
        pubSignals.merklized = inputs[1];

        for (uint256 i = 0; i < queriesCount; i++) {
            pubSignals.operatorOutput[i] = inputs[2 + i];
            pubSignals.circuitQueryHash[i] = inputs[2 + queriesCount + i];
        }
        return pubSignals;
    }

    function _getResponseFields(
        PubSignals memory pubSignals,
        Query memory query
    ) internal pure returns (ResponseField[] memory) {
        uint256 operatorCount = 0;
        for (uint256 i = 0; i < query.operator.length; i++) {
            if (query.operator[i] == 16) {
                operatorCount++;
            }
        }

        uint256 n = 1;
        ResponseField[] memory rfs = new ResponseField[](n + operatorCount);
        rfs[0] = ResponseField("linkID", pubSignals.linkID, "");

        uint256 m = 1;
        for (uint256 i = 0; i < query.operator.length; i++) {
            // TODO consider if can be more gas efficient. Check via gasleft() first
            if (query.operator[i] == 16) {
                rfs[m++] = ResponseField(
                    string(abi.encodePacked("operatorOutput_", Strings.toString(i))),
                    pubSignals.operatorOutput[i],
                    ""
                );
            }
        }

        return rfs;
    }
}

