// SPDX-License-Identifier: MIT
pragma solidity 0.8.27;
import {ECDSA} from "@openzeppelin/contracts/utils/cryptography/ECDSA.sol";
import {MessageHashUtils, EIP712} from "@openzeppelin/contracts/utils/cryptography/EIP712.sol";
import {ICrossChainProofValidator} from "../interfaces/ICrossChainProofValidator.sol";
import {IState} from "../interfaces/IState.sol";
import {Ownable} from "@openzeppelin/contracts/access/Ownable.sol";

error IdentityStateProofInvalid();
error GlobalStateProofInvalid();
error GlobalStateProofSigningAddressInvalid(address recovered);
error IdentityStateProofSigningAddressInvalid(address recovered);
error OracleTimestampCannotBeInThePast();
error OracleReplacedAtTimestampCannotBeInTheFuture();
error OracleSigningAddressShouldNotBeZero();

/**
 * @dev Contract which provides proof validation from identity state
 * and global state proofs from trusted oracle with signature from any network.
 */
contract CrossChainProofValidator is Ownable, EIP712, ICrossChainProofValidator {
    using ECDSA for bytes32;

    bytes32 public constant TYPE_HASH =
        keccak256(
            "EIP712Domain(string name,string version,uint256 chainId,address verifyingContract)"
        );

    bytes32 public constant IDENTITY_STATE_MESSAGE_TYPEHASH =
        keccak256(
            "IdentityState(uint256 timestamp,uint256 id,uint256 state,uint256 replacedAtTimestamp)"
        );

    bytes32 public constant GLOBAL_STATE_MESSAGE_TYPEHASH =
        keccak256(
            "GlobalState(uint256 timestamp,bytes2 idType,uint256 root,uint256 replacedAtTimestamp)"
        );

    bytes32 public immutable DOMAIN_SEPARATOR;

    uint256 public constant MAX_TIMESTAMP_LAG = 1 hours;
    uint256 public constant MAX_REPLACED_AT_AHEAD_OF_TIME = 5 minutes;

    address private _oracleSigningAddress;
    address private _legacyOracleSigningAddress;

    function _checkOracleSigningAddress(address oracleSigningAddress) internal pure {
        if (oracleSigningAddress == address(0)) {
            revert OracleSigningAddressShouldNotBeZero();
        }
    }

    constructor(
        string memory domainName,
        string memory signatureVersion,
        address oracleSigningAddress
    ) EIP712(domainName, signatureVersion) Ownable(msg.sender) {
        _checkOracleSigningAddress(oracleSigningAddress);
        bytes32 hashedName = keccak256(bytes(domainName));
        bytes32 hashedVersion = keccak256(bytes(signatureVersion));
        uint256 chainId = 0;
        address verifyingContract = address(0);
        DOMAIN_SEPARATOR = keccak256(
            abi.encode(TYPE_HASH, hashedName, hashedVersion, chainId, verifyingContract)
        );
        _oracleSigningAddress = oracleSigningAddress;
    }

    /**
     * @dev Gets the oracle signing address.
     * @return The oracle signing address
     **/
    function getOracleSigningAddress() public view returns (address) {
        return _oracleSigningAddress;
    }

    /**
     * @dev Sets the oracle signing address.
     * @param oracleSigningAddress The new oracle signing address
     **/
    function setOracleSigningAddress(address oracleSigningAddress) public onlyOwner {
        _checkOracleSigningAddress(oracleSigningAddress);
        _oracleSigningAddress = oracleSigningAddress;
    }

    /**
     * @dev Gets the legacy oracle signing address.
     **/
    function setLegacyOracleSigningAddress(address legacyOracleSigningAddress) public onlyOwner {
        _checkOracleSigningAddress(legacyOracleSigningAddress);
        _legacyOracleSigningAddress = legacyOracleSigningAddress;
    }

    /**
     * @dev Gets the legacy oracle signing address.
     * @return The legacy oracle signing address
     **/
    function getLegacyOracleSigningAddress() public view returns (address) {
        return _legacyOracleSigningAddress;
    }

    /**
     * @dev Disables the legacy oracle signing address.
     * This function sets the legacy oracle signing address to zero address.
     **/
    function disableLegacyOracleSigningAddress() public onlyOwner {
        _legacyOracleSigningAddress = address(0);
    }

    /**
     * @dev Verifies global state proof and signer
     * @param globalStateProof The global state proof
     * @return The result of the global state proof verification
     */
    function processGlobalStateProof(
        bytes calldata globalStateProof
    ) external view returns (IState.GlobalStateProcessResult memory) {
        IState.GlobalStateUpdate memory gsu = abi.decode(
            globalStateProof,
            (IState.GlobalStateUpdate)
        );

        (bool isValid, address recovered) = _recoverGlobalStateSigner(
            gsu.globalStateMsg,
            gsu.signature
        );
        if (!isValid) {
            revert GlobalStateProofInvalid();
        }
        if (recovered != _oracleSigningAddress && recovered != _legacyOracleSigningAddress) {
            revert GlobalStateProofSigningAddressInvalid(recovered);
        }

        return
            IState.GlobalStateProcessResult({
                idType: gsu.globalStateMsg.idType,
                root: gsu.globalStateMsg.root,
                replacedAtTimestamp: _calcReplacedAt(
                    gsu.globalStateMsg.timestamp,
                    gsu.globalStateMsg.replacedAtTimestamp
                )
            });
    }

    /**
     * @dev Verifies identity state proof and signer
     * @param identityStateProof The identity state proof
     * @return The result of the identity state proof verification
     */
    function processIdentityStateProof(
        bytes calldata identityStateProof
    ) external view returns (IState.IdentityStateProcessResult memory) {
        IState.IdentityStateUpdate memory isu = abi.decode(
            identityStateProof,
            (IState.IdentityStateUpdate)
        );

        (bool isValid, address recovered) = _recoverIdentityStateSigner(
            isu.idStateMsg,
            isu.signature
        );
        if (!isValid) {
            revert IdentityStateProofInvalid();
        }
        if (recovered != _oracleSigningAddress && recovered != _legacyOracleSigningAddress) {
            revert IdentityStateProofSigningAddressInvalid(recovered);
        }

        return
            IState.IdentityStateProcessResult({
                id: isu.idStateMsg.id,
                state: isu.idStateMsg.state,
                replacedAtTimestamp: _calcReplacedAt(
                    isu.idStateMsg.timestamp,
                    isu.idStateMsg.replacedAtTimestamp
                )
            });
    }

    /**
     * @dev See {IERC-5267}.
     */
    function eip712Domain()
        public
        view
        override
        returns (
            bytes1 fields,
            string memory name,
            string memory version,
            uint256 chainId,
            address verifyingContract,
            bytes32 salt,
            uint256[] memory extensions
        )
    {
        return (
            hex"0f", // 01111
            _EIP712Name(),
            _EIP712Version(),
            0,
            address(0),
            bytes32(0),
            new uint256[](0)
        );
    }

    function _recoverIdentityStateSigner(
        IState.IdentityStateMessage memory message,
        bytes memory signature
    ) internal view virtual returns (bool, address) {
        bytes32 hashTypedData = _hashTypedDataV4(
            keccak256(
                abi.encode(
                    IDENTITY_STATE_MESSAGE_TYPEHASH,
                    message.timestamp,
                    message.id,
                    message.state,
                    message.replacedAtTimestamp
                )
            )
        );

        (address recovered, ECDSA.RecoverError err, ) = hashTypedData.tryRecover(signature);

        return (err == ECDSA.RecoverError.NoError, recovered);
    }

    function _recoverGlobalStateSigner(
        IState.GlobalStateMessage memory message,
        bytes memory signature
    ) internal view virtual returns (bool, address) {
        bytes32 hashTypedData = _hashTypedDataV4(
            keccak256(
                abi.encode(
                    GLOBAL_STATE_MESSAGE_TYPEHASH,
                    message.timestamp,
                    message.idType,
                    message.root,
                    message.replacedAtTimestamp
                )
            )
        );

        (address recovered, ECDSA.RecoverError err, ) = hashTypedData.tryRecover(signature);

        return (err == ECDSA.RecoverError.NoError, recovered);
    }

    function _hashTypedDataV4(bytes32 structHash) internal view override returns (bytes32) {
        return MessageHashUtils.toTypedDataHash(DOMAIN_SEPARATOR, structHash);
    }

    function _calcReplacedAt(
        uint256 oracleTimestamp,
        uint256 replacedAtTimestamp
    ) internal view returns (uint256 replacedAt) {
        if (oracleTimestamp < block.timestamp - MAX_TIMESTAMP_LAG) {
            revert OracleTimestampCannotBeInThePast();
        }

        replacedAt = replacedAtTimestamp == 0 ? oracleTimestamp : replacedAtTimestamp;

        if (replacedAt > block.timestamp + MAX_REPLACED_AT_AHEAD_OF_TIME) {
            revert OracleReplacedAtTimestampCannotBeInTheFuture();
        }

        // this should never happen as block.timestamp is always greater than 0
        assert(replacedAt != 0);
    }
}

project/contracts/identitytreestore/Iden.content:
// SPDX-License-Identifier: GPL-3.0
pragma solidity 0.8.27;

import {ReverseHashLib} from "../lib/ReverseHashLib.sol";
import {PoseidonUnit2L, PoseidonUnit3L} from "../lib/Poseidon.sol";
import {IState} from "../interfaces/IState.sol";
import {IOnchainCredentialStatusResolver} from "../interfaces/IOnchainCredentialStatusResolver.sol";
import {IRHSStorage} from "../interfaces/IRHSStorage.sol";
import {Initializable} from "@openzeppelin/contracts-upgradeable/proxy/utils/Initializable.sol";

error NodeNotFound();
error InvalidStateNode();
error InvalidNodeType();
error UnsupportedLength();

/**
 * @dev Contract which provides onchain Reverse Hash Service (RHS)
 * for checking revocation status of claims.
 */
contract IdentityTreeStore is Initializable, IOnchainCredentialStatusResolver, IRHSStorage {
    /**
     * @dev Enum for node types
     */
    enum NodeType {
        Unknown,
        Middle,
        Leaf,
        State,
        Empty
    }

    /**
     * @dev Version of contract
     */
    string public constant VERSION = "1.1.0";

    /**
     * @dev Max SMT depth for the CredentialStatus proof
     */
    uint256 public constant MAX_SMT_DEPTH = 40;

    using ReverseHashLib for ReverseHashLib.Data;

    /// @custom:storage-location erc7201:iden3.storage.IdentityTreeStore.ReverseHashLibData

    // keccak256(abi.encode(uint256(keccak256("iden3.storage.IdentityTreeStore.ReverseHashLibData")) - 1)) &
    //    ~bytes32(uint256(0xff));
    // solhint-disable-next-line const-name-snakecase
    bytes32 private constant ReverseHashLibDataStorageLocation =
        0x0f7e3bdc6cc0e880d509aa1f6b8d1a88e5fcb7274e18dfba772424a36fe9b400;

    function _getReverseHashLibDataStorage() private pure returns (ReverseHashLib.Data storage $) {
        // solhint-disable-next-line no-inline-assembly
        assembly {
            $.slot := ReverseHashLibDataStorageLocation
        }
    }

    /// @custom:storage-location erc7201:iden3.storage.IdentityTreeStore.Main
    struct IdentityTreeStoreMainStorage {
        IState _state;
    }

    // keccak256(abi.encode(uint256(keccak256("iden3.storage.IdentityTreeStore.Main")) - 1)) & ~bytes32(uint256(0xff));
    // solhint-disable-next-line const-name-snakecase
    bytes32 private constant IdentityTreeStoreMainStorageLocation =
        0x95ca427007e091a13a7ccfcb233b8a2ed19d987330a248c445b1b483a35bb800;

    /// @dev Get the main storage using assembly to ensure specific storage location
    function _getIdentityTreeStoreMainStorage()
        private
        pure
        returns (IdentityTreeStoreMainStorage storage $)
    {
        // solhint-disable-next-line no-inline-assembly
        assembly {
            $.slot := IdentityTreeStoreMainStorageLocation
        }
    }

    /**
     * @dev Function to call first time for initialization of the proxy.
     * @param state The state contract address to be used to check state of the identities
     **/
    function initialize(address state) public initializer {
        IdentityTreeStoreMainStorage storage $its = _getIdentityTreeStoreMainStorage();
        ReverseHashLib.Data storage $rhl = _getReverseHashLibDataStorage();

        $its._state = IState(state);
        $rhl.hashFunction = _hashFunc;
    }

    /**
     * @dev Saves nodes array. Note that each node contains an array itself.
     * @param nodes An array of nodes
     */
    function saveNodes(uint256[][] memory nodes) external {
        return _getReverseHashLibDataStorage().savePreimages(nodes);
    }

    /**
     * @dev Returns a node by its key. Note that a node contains an array.
     * @param key The key of the node
     * @return The node
     */
    function getNode(uint256 key) public view returns (uint256[] memory) {
        uint256[] memory preim = _getReverseHashLibDataStorage().getPreimage(key);
        if (preim.length == 0) {
            revert NodeNotFound();
        }
        return preim;
    }

    /**
     * @dev Returns revocation status of a claim using given identity id and revocation nonce.
     * @param id Issuer's identifier
     * @param nonce Revocation nonce
     * @return CredentialStatus
     */
    function getRevocationStatus(
        uint256 id,
        uint64 nonce
    ) external view returns (CredentialStatus memory) {
        uint256 state = _getIdentityTreeStoreMainStorage()._state.getStateInfoById(id).state;
        return _getRevocationStatusByState(state, nonce);
    }

    /**
     * @dev Returns revocation status of a claim using given identity id, revocation nonce and state
     * @param id Issuer's identifier
     * @param state of the Issuer
     * @param nonce Revocation nonce
     * @return CredentialStatus
     */
    function getRevocationStatusByIdAndState(
        // solhint-disable-next-line no-unused-vars
        uint256 id,
        uint256 state,
        uint64 nonce
    ) external view returns (CredentialStatus memory) {
        return _getRevocationStatusByState(state, nonce);
    }

    function _getRevocationStatusByState(
        uint256 state,
        uint64 nonce
    ) internal view returns (CredentialStatus memory) {
        uint256[] memory roots = getNode(state);
        if (_nodeType(roots) != NodeType.State) {
            revert InvalidStateNode();
        }

        CredentialStatus memory status = CredentialStatus({
            issuer: IdentityStateRoots({
                state: state,
                claimsTreeRoot: roots[0],
                revocationTreeRoot: roots[1],
                rootOfRoots: roots[2]
            }),
            mtp: _getProof(roots[1], nonce)
        });

        return status;
    }

    function _getProof(uint256 root, uint256 index) internal view returns (Proof memory) {
        uint256[] memory siblings = new uint256[](MAX_SMT_DEPTH);
        // Solidity does not guarantee that memory vars are zeroed out
        for (uint256 i = 0; i < MAX_SMT_DEPTH; i++) {
            siblings[i] = 0;
        }

        Proof memory proof = Proof({
            root: root,
            existence: false,
            siblings: siblings,
            index: index,
            value: 0,
            auxExistence: false,
            auxIndex: 0,
            auxValue: 0
        });

        uint256 nextNodeHash = root;
        uint256[] memory children;

        ReverseHashLib.Data storage $ = _getReverseHashLibDataStorage();

        for (uint256 i = 0; i <= MAX_SMT_DEPTH; i++) {
            children = $.hashesToPreimages[nextNodeHash];

            NodeType nodeType = _nodeType(children);
            if (nodeType == NodeType.Empty) {
                break;
            }

            if (nodeType == NodeType.Leaf) {
                if (children[0] == index) {
                    proof.existence = true;
                    proof.value = children[1];
                    break;
                }

                proof.auxExistence = true;
                proof.auxIndex = children[0];
                proof.auxValue = children[1];
                break;
            } else if (nodeType == NodeType.Middle) {
                if ((proof.index >> i) & 1 == 1) {
                    nextNodeHash = children[1];
                    proof.siblings[i] = children[0];
                } else {
                    nextNodeHash = children[0];
                    proof.siblings[i] = children[1];
                }
            } else {
                revert InvalidNodeType();
            }
        }

        return proof;
    }

    function _nodeType(uint256[] memory node) internal pure returns (NodeType) {
        if (node.length == 2) {
            return NodeType.Middle;
        }

        if (node.length == 3 && node[2] == 1) {
            return NodeType.Leaf;
        }

        if (node.length == 3) {
            return NodeType.State;
        }

        if (node.length == 0) {
            return NodeType.Empty;
        }

        return NodeType.Unknown;
    }

    function _hashFunc(uint256[] memory preimage) internal pure returns (uint256) {
        if (preimage.length == 2) {
            return PoseidonUnit2L.poseidon([preimage[0], preimage[1]]);
        }
        if (preimage.length == 3) {
            return PoseidonUnit3L.poseidon([preimage[0], preimage[1], preimage[2]]);
        }
        revert UnsupportedLength();
    }
}

